// HomePage.js
import React from 'react';

function Feature({ imageUrl, title, description }) {
  return (
    <div className="feature">
      <img src={imageUrl} alt={title} />
      <h3>{title}</h3>
      <p>{description}</p>
    </div>
  );
}

function HomePage() {
  return (
    <div className="home-page">
      <h1>Welcome to Little Palm</h1>

      <div className="features">
        <Feature
          imageUrl="image1.jpg"
          title="Exquisite Cuisine"
          description="Indulge in a variety of exquisite dishes prepared by our talented chefs."
        />

        <Feature
          imageUrl="image2.jpg"
          title="Cozy Ambiance"
          description="Experience a cozy and inviting atmosphere that enhances your dining pleasure."
        />

        <Feature
          imageUrl="image3.jpg"
          title="Exceptional Service"
          description="Our dedicated staff is committed to providing exceptional service to make your visit memorable."
        />
      </div>
    </div>
  );
}

export default HomePage;
